import { useEffect, useState } from "react";
import { Register, ErrorMessage, SeccessMessage } from '../service'
function RegisterForm() {
    const [username, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [showErr, setShowErr] = useState(false);
    const [err, setError] = useState('')

    const registerUser = async (e) => {
        e.preventDefault();
        if (username === '' || password === '') {
            console.log('Please fill in all fields')
        } else {
            try {
                const res = await Register({
                    username, password
                });
                SeccessMessage(res.data.msg)
            } catch (err) {
                setShowErr(true)
                //ErrorMessage(setError(err))
            }
        }
    }

    useEffect(() => {
        console.log(username, password)
    }, [username, password])
    const errShow = (
        showErr ?
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
                <strong className="font-bold">Holy smokes!</strong>
                <span className="block sm:inline">Username already exists.</span>
                <span className="absolute top-0 bottom-0 right-0 px-4 py-3"
                    onClick={setShowErr(false)}>
                    <svg className="fill-current h-6 w-6 text-red-500" role="button" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><title>Close</title><path d="M14.348 14.849a1.2 1.2 0 0 1-1.697 0L10 11.819l-2.651 3.029a1.2 1.2 0 1 1-1.697-1.697l2.758-3.15-2.759-3.152a1.2 1.2 0 1 1 1.697-1.697L10 8.183l2.651-3.031a1.2 1.2 0 1 1 1.697 1.697l-2.758 3.152 2.758 3.15a1.2 1.2 0 0 1 0 1.698z" /></svg>
                </span>
            </div>
            : null
    )


    return (
        <>
            <div >{errShow}</div>
            <div className='h-screen flex bg-gradient-to-r from-indigo-500'>

                <div className='w-full max-w-md m-auto bg-white rounded-lg border border-primaryBorder shadow-default py-10 px-16'>
                    <h1 className='text-2xl font-medium text-primary mt-4 mb-12 text-center'>
                        Sign in to your account 🔐
                    </h1>

                    <form onSubmit={registerUser}>
                        <div>
                            <label htmlFor='username'>UserName</label>
                            <input
                                value={username}
                                onChange={(e) => setUserName(e.target.value)}
                                type='text'
                                className={`w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4`}
                                id='username'
                                placeholder='Your Username'
                            />
                        </div>
                        <div>
                            <label htmlFor='password'>Password</label>
                            <input
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                type='password'
                                className={`w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4`}
                                id='password'
                                placeholder='Your Password'
                            />
                        </div>

                        <div className="md:flex md:items-center">
                            <div className="md:w-1/3"></div>
                            <div className="md:w-2/3">
                                <button className="shadow bg-purple-500 hover:bg-purple-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded" type="submit">
                                    Sign Up
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
            </div></>
    );
}

export default RegisterForm;
